mapper.select:
SELECT A.CODE_CD , A.CODE_NM , A.CODE_LVL , A.P_CODE_CD , B.CODE_NM P_CODE_NM , A.DSP_ORDER , A.USE_YN , A.EXP_FR_DT , A.EXP_TO_DT , A.ATTR1_JSON , A.ATTR1_VAL , A.ATTR2_JSON , A.ATTR2_VAL , A.ATTR3_JSON , A.ATTR3_VAL , A.ATTR4_JSON , A.ATTR4_VAL , A.ATTR5_JSON , A.ATTR5_VAL , A.ATTR6_JSON , A.ATTR6_VAL , A.ATTR7_JSON , A.ATTR7_VAL , A.ATTR8_JSON , A.ATTR8_VAL , A.ATTR9_JSON , A.ATTR9_VAL , A.ATTR10_JSON , A.ATTR10_VAL FROM CMN_CODE A , CMN_CODE B WHERE B.CODE_CD = A.P_CODE_CD
AND A.P_CODE_CD = #{P_CODE_CD}
AND INSTR(CONCAT(IFNULL(A.CODE_NM,''),IFNULL(A.ATTR1_VAL,''),IFNULL(A.ATTR2_VAL,''),IFNULL(A.ATTR3_VAL,''),IFNULL(A.ATTR4_VAL,''),IFNULL(A.ATTR5_VAL,'')), #{SEARCH_TEXT}) > 0
AND A.USE_YN = #{USE_YN}
AND A.EXP_FR_DT <= STR_TO_DATE(#{PERIOD[1]}, '%Y-%m-%d') AND A.EXP_TO_DT >= STR_TO_DATE(#{PERIOD[0]}, '%Y-%m-%d')
ORDER BY A.CODE_LVL, A.P_CODE_CD, A.DSP_ORDER
